import jwt_decode from 'jwt-decode'

export default function authMiddleware(router) {
  router.beforeEach((to, from, next) => {
    let requiresAuth = to.matched.some(record => record.meta.requiresAuth);
    let isGuest = to.matched.some(record => record.meta.isGuest);
    let token = sessionStorage.getItem('Token');
    console.log('rA: ', requiresAuth, 'iG: ', isGuest, 'token: ', token)
    if (requiresAuth && !token){
        return next({path: '/login'});
    } else if (token && isGuest){
        return next({path: '/admin/overview'});
    } else {
    let decodedToken = jwt_decode(token);
    let { user, role } = decodedToken;
    let { role: metaRole } = to.meta;
    console.log("Decoded Token: ", decodedToken);
    console.log("role: ", role, "  ||  user: ", user);
    console.log("metaRole: ", metaRole);
    let userId = sessionStorage.getItem('UserID');
    return next();
    }

  })
}
